INSERT INTO `xiecheng_lht` VALUES ('22345', '莱茵河畔吕德斯海姆正德皇帝', 'https://images4.c-ctrip.com/target/t1/headphoto/646/318/109/a865fe5f5465407d8befd5dc487a8554_C_180_180.jpg', '', '鹿回头风景区位于三亚市南3公里处，是海南岛最南端的山头。这座山三面临海，状似坡鹿，高275米，登上鹿回头山顶，三亚市全景尽收眼底。现在鹿回头[1]山顶已建成一座美丽的山顶公园，并根据美丽的传说在山上雕塑了一座高12米、长9米，宽4.9米的巨型雕像。三亚市也因此被人们称为“鹿城”。 展开更多', '2018-03-06', '2018-10-17 08:48:48', '2018-03-06 详情 举报 有用 （0）', '有用 （0）', '3230', '75996');
