INSERT INTO `xiecheng_lht` VALUES ('23675', '159****7735', 'https://images4.c-ctrip.com/target/t1/headphoto/857/872/237/edc9e981b00e436a8dcee595001d62fa_C_180_180.jpg', '', '游览车坐到山顶再逛一圈，有点山坡，再坐车下去出口，半途下了雷阵雨，凉快了一阵，可惜太阳一出又马上热起来了，主要以爱情为主题的一个景点，有月老，夫妻树等，可以看大东海风景，三亚地标性建筑凤凰岛梧桐酒店貌似叫，可惜没定那里住 展开更多', '2017-06-23', '2018-10-17 08:48:48', '2017-06-23 详情 举报 有用 （0）', '有用 （0）', '3230', '75996');
