INSERT INTO `xiecheng_ns` VALUES ('3642', 'M22****3696', 'https://images4.c-ctrip.com/target/t1/headphoto/882/320/089/158cae5ddb6143c0b6ae18523614cf08_C_180_180.jpg', '', '取票方便，因为人多会排队但耗时不长。108米南海观音很壮观很慈祥，可以登上莲花台抱佛脚，不会另外收费，只是因为限制登台人数需要排队。南山寺门前的观景平台可以看到南海观音的侧面和壮阔蔚蓝的海面，拍照很美。南山景区很大最好买观光车票。 展开更多', '2017-07-12', '2018-10-17 08:48:48', '2017-07-12 详情 举报 有用 （0）', '有用 （0）', '3227', '75993');
