INSERT INTO `xiecheng_ns` VALUES ('3749', '320****835', 'https://images4.c-ctrip.com/target/Z80n0h0000008pom0BE59_C_180_180.jpg', '', '南山寺景区里面的景点逛了海上观音和南山寺已经足够，别的地方意义不大，买一张30的电瓶车票比较好省点体力可以同一天去下大小洞天或者天涯海角之一 展开更多', '2017-06-09', '2018-10-17 08:48:48', '2017-06-09 详情 举报 有用 （0）', '有用 （0）', '3227', '75993');
