INSERT INTO `xiecheng_ns` VALUES ('4418', '大鹏', 'https://images4.c-ctrip.com/target/fd/headphoto/g3/M02/9B/25/CggYGlbEFnaAKWc3AAA-LrBp8_s180_C_180_180.jpg', '景色：5  趣味：5  性价比：5   ', '非常漂亮的景点，尤其是上到观音上去抱佛脚，上面的景色更美好！ 展开更多', '2016-02-25', '2018-10-17 08:48:48', '2016-02-25 详情 举报 有用 （0）', '有用 （0）', '3227', '75993');
