INSERT INTO `xiecheng_ns` VALUES ('4421', '苦-瓜', 'https://images4.c-ctrip.com/target/t1/headphoto/857/872/237/edc9e981b00e436a8dcee595001d62fa_C_180_180.jpg', '景色：4  趣味：4  性价比：4    建议游览4小时', '我们去的时候南海观音还在修，还没修好，只能远观，很高大，南山的植被很好，里面的自助餐非常不错，都是素食，而且色香味都很好，好多以为是肉，其实不是，好吃，里面的草坪质量不错，坐上去很舒服，视野开阔让人心情愉快。 展开更多', '2014-10-24', '2018-10-17 08:48:48', '2014-10-24 详情 举报 有用 （1）', '有用 （1）', '3227', '75993');
