INSERT INTO `xiecheng_ns` VALUES ('4632', '蜜蜜7s', 'https://images4.c-ctrip.com/target/Z80o0k000000celnp83FB_C_180_180.jpg', '', '在门口的时候我问了一个工作人员哪里买电瓶车票！然后他就顺带给我们介绍了一下里面去哪些的路线！他叫我们可以先去金玉观音，说哪里比较灵验！再去海上观音！但他没有告诉我们金玉观音要在单独购票才能参观！害我们又倒回来去海上观音！灵验就是因为要重新二次消费咯？ 展开更多', '2017-10-05', '2018-10-17 08:48:48', '2017-10-05 详情 举报 有用 （0）', '有用 （0）', '3227', '75993');
