INSERT INTO `xiecheng_qsw` VALUES ('6936', '有谁迷路了吗', 'https://images4.c-ctrip.com/target/Z80n0d0000006wo2o426A_C_180_180.jpg', '', '清水湾，沙滩细腻，海水清澈，周边安静，是优质度假区！近几年被游客悉知，来此游玩儿的人越来越多。其中，雅居乐己是成熟社区，周边设施完善！推荐你来！ 展开更多', '2017-01-27', '2018-11-05 09:24:11', '2017-01-27 详情 举报 有用 （6）', '有用 （6）', '107590', '90753');
