INSERT INTO `xiecheng_qxl` VALUES ('34', '298****978', 'https://images4.c-ctrip.com/target/t1/headphoto/057/777/943/0b93f8268d5546308915f4f9fcaa9483_C_180_180.jpg', '景色：5  趣味：3  性价比：4   ', '保亭县的景点之一，热带雨林风格，风景秀丽，还不错，爬到山顶不容易，约2300米。性价比高，人不太多，逛起来较为舒适。值得一去。不是自驾的话，交通稍不便。 展开更多', '2017-01-30', '2018-11-02 16:12:03', '2017-01-30 详情 举报 有用 （2）', '有用 （2）', '145186', '10521117');
