INSERT INTO `xiecheng_qxl` VALUES ('218', '_jsz****0339', 'https://images4.c-ctrip.com/target/t1/headphoto/757/757/889/3b86691007a54fe684f07e198432d09b_C_180_180.jpg', '', '酒店还是一如既往的好。设备稍显陈旧，人员服务也稍显懈怠，办理入住效率较低。酒店的私属沙滩很棒，沙很细。着重一谈的还是酒店的餐厅。早餐比较丰盛，在同级别酒店中还算不错。泰餐厅尤其的好，鲜虾冬阴功汤、虾饼、咖喱牛腩等等各种咖喱都非常的棒，很地道， 展开更多', '2016-12-07', '2018-11-02 16:12:03', '2016-12-07 详情 举报 有用 （0）', '有用 （0）', '145186', '10521117');
