INSERT INTO `xiecheng_tyhj` VALUES ('134', '飛翔牛牛', 'https://images4.c-ctrip.com/target/Z80a0p000000fze56D0E5_C_180_180.jpg', '景色：4  趣味：4  性价比：4   ', '早上到的第一站旅游景点，取票方便快捷。进入景区后到海边玩了会沙子有点粗，建议在此处不要玩时间长，在直接去到天涯海角景点处那里沙滩细，海面宽阔 景色比入园第一处景点好，个人建议。 展开更多', '2017-08-17', '2018-10-17 08:48:48', '2017-08-17 详情 举报 有用 （2）', '有用 （2）', '3222', '10524171');
