INSERT INTO `xiecheng_tyhj` VALUES ('598', 'SillySallyia', 'https://images4.c-ctrip.com/target/Z80j0i0000009t0kt3280_C_180_180.jpg', '景色：5  趣味：5  性价比：4   ', '从三亚红树林酒店坐出租车去的，50元。路上风景挺好，海边的沙是比较粗的那种，踩在脚底有点疼，不过海水还是很清澈的。因为礁石比较多，不能下海游泳，适合情侣去游玩。 展开更多', '2017-07-31', '2018-10-17 08:48:48', '2017-07-31 详情 举报 有用 （3）', '有用 （3）', '3222', '10524171');
