INSERT INTO `xiecheng_tyhj` VALUES ('2062', '小博士1992', 'https://images4.c-ctrip.com/target/t1/headphoto/177/080/529/285e9abeede347d9a7c1cc5e1b4c9287_C_180_180.jpg', '', '去之前看有的评论就几块大石头，有点担心不值，但是去了才发现这里确实不错，一开始买了游船门票的套票，到了告知风浪太大停航了，没想到到了下午天空放晴，顺利登船游览，很棒，沙滩的沙子有点扎脚，但是那几块石头确实值得合影留念，不拍都对不起三亚行啊！公园里零食价格合理，喜欢天涯海角石旁边的拉长冰激凌，味道好棒，就是有点小贵，水果十块一大碗，不错，玩的很开心 展开更多', '2017-09-03', '2018-10-17 08:48:48', '2017-09-03 详情 举报 有用 （0）', '有用 （0）', '3222', '10524171');
