INSERT INTO `xiecheng_tyhj` VALUES ('2908', 'M24****1952', 'https://images4.c-ctrip.com/target/t1/headphoto/882/320/089/158cae5ddb6143c0b6ae18523614cf08_C_180_180.jpg', '', '真心很差！零点很小。就几块在海边的大石头。海滩一点都不好。全是贝壳特别容易划脚。都没有亚龙湾免费海滩好。景点最垃圾的地方在于出口的位置。开回绕行。全是卖东西的地方。必须全部走完才能出去。足足得走20分钟。本来逛一天的景点就够累的了。结果买了水果吃还算是坏的。真心不推荐去。 展开更多', '2017-12-11', '2018-10-17 08:48:48', '2017-12-11 详情 举报 有用 （0）', '有用 （0）', '3222', '10524171');
