INSERT INTO `xiecheng_tyhj` VALUES ('2915', 'cou****_yh', 'https://images4.c-ctrip.com/target/t1/headphoto/724/518/802/e201a429d7a54f72ade0df32a3d59b2f_C_180_180.jpg', '', '我们到达三亚凤凰机场准备回上海了，办好登机牌和行李托运一看还有2个小时才飞，看地图天涯海角离机场很近，我们就打车去了，天涯的海水依然那么清澈蔚蓝，和亚龙湾不同的是海边各种形状的石头比较多，我看到最大的应该是天涯那块石头吧，由于时间匆忙没找到海角就返回机场了，离起飞就剩十几分钟，好险 展开更多', '2017-10-17', '2018-10-17 08:48:48', '2017-10-17 详情 举报 有用 （0）', '有用 （0）', '3222', '10524171');
