INSERT INTO `xiecheng_tyhj` VALUES ('2958', '幽幽锦蝶', 'https://images4.c-ctrip.com/target/t1/headphoto/258/512/704/6bce7506eecc48f8a4a6d3177e0acf24_C_180_180.jpg', '', '原以为人多的不行，其实还可以接受，带着老人直接买了电瓶车票，也可以随时上下车购买，很方便。园区内管理很好很干净。出门时要经过一个长长的购物通道，有纪念品也有水果等东西很多。园区外的公交车出租车就不那么美好了，人多还拒载。 展开更多', '2017-04-11', '2018-10-17 08:48:48', '2017-04-11 详情 举报 有用 （0）', '有用 （0）', '3222', '10524171');
