INSERT INTO `xiecheng_wbf` VALUES ('683', '周晨', 'https://images4.c-ctrip.com/target/t1/headphoto/694/956/836/235bcba364c24f06bf23420d3637c9fe_C_180_180.jpg', '', '先走的中轴线，几个大殿，又从西顺时针绕圈子，全部走完3个半小时。全价84，优惠票39，携程28。对于道教没有太多感觉，山上的风景还可以。从定安汽车站坐车，到南丽湖的车，中途在文笔峰门口下车。回去的车要6点才有，只有做三轮摩托到高速路口等车，摩托车12可以坐3个人，回去的车每人5.5。 展开更多', '2017-12-31', '2018-11-02 16:12:03', '2017-12-31 详情 举报 有用 （1）', '有用 （1）', '131946', '10546737');
