INSERT INTO `xiecheng_wbf` VALUES ('759', 'TONGCHENG_USER', 'https://images4.c-ctrip.com/target/fd/headphoto/g3/M02/E5/D1/CggYGVaXFKmANau1AAAV_4N61J4918_C_180_180.jpg', '', '整个山上风景不错，就是交通非常不方便。从海口自己搭车前往定安，后按照热心驾驶员的提示下车，搭乘三轮柴油车（三脚猫）前往景点又花了8元。返程再花8元，高速路口，花了14元搭上回海口的车。到了景点，首先惊叹于其厕所，环境太好了，堪称五星级厕所！景区内，一直有一种香味，后来才知道是一种纯植物的香，央视报道过的，做香的材料全部可以食用。里面的购物点好多门都关着。在砗磲店里，好好见识了一下高档的砗磲。该处还是电影《私人订制》的拍摄地呢！ 展开更多', '2015-04-14', '2018-11-02 16:12:03', '2015-04-14 详情 举报 有用 （2）', '有用 （2）', '131946', '10546737');
