INSERT INTO `xiecheng_wzz` VALUES ('24582', 'Chalicee', 'https://images4.c-ctrip.com/target/t1/headphoto/207/494/838/fa687736cf3f484480526cfc0eb6c073_C_180_180.jpg', '景色：5  趣味：5  性价比：3   ', '很漂亮的海岛，我是五月份的时候去的，虽然天气很热，但是感觉海水有点儿凉，所以没敢下水，不过还是有很多人游泳和潜水的。我印象最深刻的就是蜈支洲岛上的细白的沙子，海边很多白色的淡黄色的珊瑚石，太漂亮了！ 展开更多', '2018-08-29', '2018-10-17 08:48:48', '2018-08-29 详情 举报 有用 （0）', '有用 （0）', '3244', '10558614');
