INSERT INTO `xiecheng_wzz` VALUES ('25142', '雪人的泪', 'https://images4.c-ctrip.com/target/Z80s0v000000k11at8F1D_C_180_180.jpg', '景色：5  趣味：4  性价比：4   ', '游客离岛后岛上很安静、很美（可以很自由的拍照，不怕都是人哦）难怪被誉为中国的马尔代夫呢！由于带着宝宝去的，没有体验那些水上活动！情侣们可以来尝试下哦！个人建议住一晚岛上的珊瑚酒店哦！ 展开更多', '2015-06-02', '2018-10-17 08:48:48', '2015-06-02 详情 举报 有用 （1）', '有用 （1）', '3244', '10558614');
