INSERT INTO `xiecheng_xd` VALUES ('18039', '飞跃云层的神奇', 'https://images4.c-ctrip.com/target/Z8070c00000061seo2BA1_C_180_180.jpg', '景色：5  趣味：5  性价比：5   ', '三亚西部的海岛，坐船前往，环境优美，碧海蓝天，岛上有很多海上运动项目如香蕉船，摩托艇，滑翔伞等，游客不多，很舒服的环境，夕阳西下，在这里可以吃到自助烧烤大餐！ 展开更多', '2018-02-02', '2018-10-17 08:48:48', '2018-02-02 详情 举报 有用 （2）', '有用 （2）', '48963', '10558942');
