INSERT INTO `xiecheng_xd` VALUES ('18058', '一切兜好', 'https://images4.c-ctrip.com/target/fd/headphoto/g4/M05/31/8C/CggYHVYD1qyACczOAADpfiB15uc821_C_180_180.jpg', '', '潜水我推荐大家去西岛。西岛的西面海域是专设的潜水海域，海水清澈湛蓝，能见度高。海底生物种类繁多，您在这里可以观赏到美轮美奂的鹿角珊瑚、冠状珊瑚和五彩斑斓的狮子鱼、小丑鱼、青衣、神仙鱼等热带鱼儿以及海星、海葵、海胆、海螺等种类繁多的海洋生物。 展开更多', '2018-03-12', '2018-10-17 08:48:48', '2018-03-12 详情 举报 有用 （3）', '有用 （3）', '48963', '10558942');
