INSERT INTO `xiecheng_xl` VALUES ('7886', 'cute baby traveling', 'https://images4.c-ctrip.com/target/fd/headphoto/g5/M01/E9/F8/CggYr1cInfCAej9oAADXaSprr_M397_C_180_180.jpg', '', '说实话不太值得去的一个景点，比较鸡肋。我们坐游览车逛了一圈，时间大概20分钟，门口出来就免费和茶和咖啡，倒是挺好喝的，买了几袋。 展开更多', '2017-12-17', '2018-11-05 09:24:11', '2017-12-17 详情 举报 有用 （4）', '有用 （4）', '3200', '75967');
