INSERT INTO `xiecheng_xl` VALUES ('8080', '阿索洛', 'https://images4.c-ctrip.com/target/t1/headphoto/982/089/752/f8c7b21182c94524ac4df328fdc7b3dc_C_180_180.jpg', '', '植物很少。就只有椰树，可可树，胡椒几种。珍稀植物一样也没有。门口放的各种特别植物的照片，一样也没有。45的门票太坑了。到最后就是卖东西的。咖啡什么，可可之类，茶。园内非常小。跟着讲解不到半小时就逛完了。全是老人团。如果有购物需求可以去。如果是看植物，超级不推荐。 展开更多', '2018-09-14', '2018-11-05 09:24:11', '2018-09-14 详情 举报 有用 （0）', '有用 （0）', '3200', '75967');
