INSERT INTO `xiecheng_xl` VALUES ('8555', '春天的雪', 'https://images4.c-ctrip.com/target/t1/headphoto/595/238/966/4b29803af6c44da7a2193587e40aa2f1_C_180_180.jpg', '', '感觉非常好，座着电瓶车转了一大圈，中间给我们下来拍照，司机又是讲解员，给我们介绍了很多从来没有见过的植物。增加了很多乐趣！就是网络订票一定要等一个小时后才可以进，这个规定不好，浪费时间。还好我们去了兴隆咖啡园，这里也是不错的！ 展开更多', '2016-11-11', '2018-11-05 09:24:11', '2016-11-11 详情 举报 有用 （0）', '有用 （0）', '3200', '75967');
