INSERT INTO `xiecheng_ylw` VALUES ('9192', '大理阿尔杰农', 'https://images4.c-ctrip.com/target/Z8050x000000l7330549B_C_180_180.jpg', '景色：5  趣味：5  性价比：5   ', '素有“天下第一湾”的亚龙湾，每次来三亚怎能不去，看过很多地方不同的热带雨林，唯独喜欢这里的，没有为什么。这里也是冯小刚当年拍《非诚勿扰2》的取景地，一直名声在外，来了不会错。 展开更多', '2018-09-21', '2018-10-17 08:48:48', '2018-09-21 详情 举报 有用 （0）', '有用 （0）', '74247', '89388');
