INSERT INTO `xiecheng_ylw` VALUES ('9255', '周游列国', 'https://images4.c-ctrip.com/target/Z8070a0000004m9t7BB14_C_180_180.jpg', '景色：5  趣味：5  性价比：5   ', '热带森林公园有很好的景色，百分之百的森林履盖率。园内山顶有沧海楼可俯看整个亚龙湾，景色美极了。在满山绝色中，隐约可见分布着十几处房间，这是森林公园渡假村酒店，住在森林中，真是一种享受。 展开更多', '2017-02-05', '2018-10-17 08:48:48', '2017-02-05 详情 举报 有用 （2）', '有用 （2）', '74247', '89388');
INSERT INTO `xiecheng_ylw` VALUES ('9256', 'elaine101180', 'https://images4.c-ctrip.com/target/Z80f0j000000a5wiq327A_C_180_180.jpg', '', '还不错哦！能悠闲的玩一天，四个点各有特色！玻璃栈道确实不错，索桥的话一般吧，没人的时候能体验下，有人的话也没啥意思排队过桥的感觉！不过风景真的不错！下次去想去尝试索道了！留点遗憾吧！ 展开更多', '2018-10-01', '2018-10-17 08:48:48', '2018-10-01 详情 举报 有用 （0）', '有用 （0）', '74247', '89388');
