INSERT INTO `xiecheng_ylw` VALUES ('10132', 'M34****23', 'https://images4.c-ctrip.com/target/t1/headphoto/272/077/772/76b262d0cc24492abc5c6784c903c2ab_C_180_180.jpg', '景色：5  趣味：5  性价比：5   ', '太值得去了 不建议跟团 自己买票过去 我住在三亚湾 坐27路直达 里面景色超美 我们自己玩的 爬山 找洞 还有石桥 各种美 在爬山过程中见识了很多特别的热带树 不枉此行 哦 游览车很屌 爱死了 下来上去 总共坐了两遍 展开更多', '2015-10-07', '2018-10-17 08:48:48', '2015-10-07 详情 举报 有用 （0）', '有用 （0）', '74247', '89388');
