INSERT INTO `xiecheng_ylw` VALUES ('10378', 'ayumi', 'https://images4.c-ctrip.com/target/t1/headphoto/757/757/889/3b86691007a54fe684f07e198432d09b_C_180_180.jpg', '', '森林氧吧还不错，中午那个文昌鸡套餐也性价比还可以，就是商业化氛围浓厚，一个椰子冻要50，买两个60也不愿意，最后游览车送到终点围着一群人要问你打的不？免费送海底世界但必须买水上项目，想打滴滴，直接说他们上不来的，好坑啊，不会再去了 展开更多', '2017-09-25', '2018-10-17 08:48:48', '2017-09-25 详情 举报 有用 （2）', '有用 （2）', '74247', '89388');
