INSERT INTO `xiecheng_ylw` VALUES ('10836', '戴黑框的熊猫', 'https://images4.c-ctrip.com/target/t1/headphoto/857/872/237/edc9e981b00e436a8dcee595001d62fa_C_180_180.jpg', '', '开车去的，进公园前的那段路，两边都是树，还有稻田，景色很赞。里面每个站点都有游览车接送，很方便，尤其是到达号称自然空调的塔顶，超级凉快的，景色很美，喜欢拍照的朋友一定不能错过试婚房和教堂的部分，景区里补给站很多，建议可以轻装上阵，因为要爬山。祝去玩的朋友们要玩好，喝好。 展开更多', '2018-05-03', '2018-10-17 08:48:48', '2018-05-03 详情 举报 有用 （0）', '有用 （0）', '74247', '89388');
