INSERT INTO `xiecheng_ylwbhly` VALUES ('6713', '_CFT01****1381856', 'https://images4.c-ctrip.com/target/t1/headphoto/293/406/308/22631089f44a497495c005fce85f6a6f_C_180_180.jpg', '景色：3  趣味：3  性价比：3   ', '携程订票96，现场地图无，拍图备用，风大，无阳光，照相效果不好，海边没过去，贝壳馆没人，喜欢带个纪念品也行，坐电瓶车到登山隧道，人少电梯也不开，半小时到山顶，锻炼身体，上边能看亚龙湾，有蝶茧木屋，不让上，风大晃悠，婚纱照不少，坐车到蝴蝶馆，没看到活蝴蝶，到溪谷，走一段就出来了，看到出口就出来了，没原路回入口，感受一下，说起来进来过。 展开更多', '2017-02-10', '2018-11-05 09:24:11', '2017-02-10 详情 举报 有用 （7）', '有用 （7）', '1791269', '24062199');
