INSERT INTO `xiecheng_ynd` VALUES ('15066', '万红秀', 'https://images4.c-ctrip.com/target/Z80e0x000000l02is9735_C_180_180.jpg', '', '非常美的热带雨林，我玩了悬崖千秋，滑索，穿越丛林，个人建议带6岁以下小孩去的，不要玩踏瀑戏水，不适合太小的孩子，也不建议玩穿越丛林，很消耗体力，6岁以下的孩子玩会很累，很美好的一次旅行 展开更多', '2018-10-05', '2018-10-17 08:48:48', '2018-10-05 详情 举报 有用 （0）', '有用 （0）', '55820', '85542');
