INSERT INTO `xiecheng_ynd` VALUES ('15158', 'yanpinghe', 'https://images4.c-ctrip.com/target/Z80k0j000000au9qsE109_C_180_180.jpg', '景色：5  趣味：5  性价比：4   ', '由于时间关系，只玩了踏瀑戏水一个项目，类似溯溪。全程都有教练和志愿者保护，很安全！花了两个小时多一点点！进景区买的成人套票255，儿童210，由电瓶车接到起点，这个钱花得值！特别适合小孩子！！嗨森啊 展开更多', '2017-08-14', '2018-10-17 08:48:48', '2017-08-14 详情 举报 有用 （3）', '有用 （3）', '55820', '85542');
