INSERT INTO `meituan_binglanggu` VALUES ('755', 'qfm273243522', '127519001', '', '极具民族特色，非常值得去看看。里面挺大的，东西内容也多，如果慢慢看可以有一天的，里面的东西也不贵，尤其是吃的，我们在出口的地方要了一份酸辣粉、粥和粉汤便宜的不要不要的。但是散客请导游要注意了，一般价格是80元一位导游，可能是导游想快点结束好再接一单吧，很多东西都是匆匆而过，听的人云里雾里的。我联系给导游加点钱，让她好好讲解一下，毕竟很多都是黎族文化，不仔细听讲，很难弄明白啊！', '2018-03-04 14:20:06', '-1488427342:http://p0.meituan.net/shaitu/b0869643a38cb676ae8902a286f40b002883629.jpg;\n-1488427343:http://p0.meituan.net/shaitu/f4a93592f4e290a592eec5e265d483ba3427807.jpg;\n-1488327205:http://p0.meituan.net/shaitu/0fc824e420e7f2a4fa9f589cb442290c3183153.jpg;\n-1488327206:http://p0.meituan.net/shaitu/f16a635692a8dc5a9da213f4615161382738332.jpg;\n', 'None');
