INSERT INTO `meituan_binglanggu` VALUES ('2135', 'LXW13428780766', '57887893', 'https://img.meituan.net/avatar/da885099748d42c54042f393b6c89eaa73386.jpg', '非常不错?，感受苗族跟黎族的文化。里面讲解的的导游也非常幽默。环保空气各方面都非常棒。至于吃的肯定比外面贵，必竟景区。建议自己带点干粮跟水进去。要穿长裤，山上蚊虫多，但是进苗族前会在你脸上擦一点药可以驱蚊虫用的，还真挺管用。', '2017-04-30 23:03:12', '-1886314442:http://p0.meituan.net/shaitu/6a575ed618037da8aa446cdda8b6968e588627.jpg;\n-1885878055:http://p0.meituan.net/shaitu/d04194f2e17f01f691e46d9705491519312125.jpg;\n-1886461822:http://p0.meituan.net/shaitu/488c169b0347d4036c620b765a63be2b653278.jpg;\n-1886461806:http://p0.meituan.net/shaitu/7cc2728851c85eb0fd7c9962373dd36f601253.jpg;\n-1886405542:http://p0.meituan.net/shaitu/5e4d56d55deedc4dbefac8ac874eda52616380.jpg;\n-1885905907:http://p0.meituan.net/shaitu/0d253d27dc9b6d8923a81247ba48ceaf388167.jpg;\n-1885340044:http://p0.meituan.net/shaitu/52fa10cc3429541cea0e7dc4d28f0770506852.jpg;\n-1885867497:http://p0.meituan.net/shaitu/34b97a8b68c15837be4b0ef06a95e58c655850.jpg;\n', 'None');
