INSERT INTO `meituan_binglanggu` VALUES ('3078', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '服务非常差，景区组织指示一团糟，态度不好从买票到里面的导游，美团购票了，到景区说是还有表演套票，想买得退网票，巴拉巴拉，问个卫生间跟没听见似的，游览2个地方后就是水果特产饭店一条街，自助千万不要吃，恶心，卫生差，苍蝇落桌子上都被粘住了，不值得千里迢迢一游，', '2017-01-15 14:19:22', '-1898913733:http://p0.meituan.net/shaitu/ba6025a7891ac7a9feae84361b333285159020.jpg;\n', 'None');
