INSERT INTO `meituan_binglanggu` VALUES ('3450', 'youk488', '78010868', '', '里面没什么游乐设施和项目，主要以人文风情为主，表演觉得有些无聊，景区蚊子挺多被咬了好几个地方，价格一般般，总的来说觉得一般。里面那些老奶奶织出来的东西觉得不错，但是价格有些高，不过也值这么多钱，今天人不多，排队速度挺快。', '2016-11-17 22:47:10', '', 'None');
