INSERT INTO `meituan_binglanggu` VALUES ('3606', 'mlw436955595', '197213307', 'https://img.meituan.net/avatar/a9a5fd813d69fddc3a6223b166fe0840411702.jpg', '来三亚我觉得槟榔谷黎苗文化旅游区我特别喜欢了 和爸爸妈妈在里边玩了一整天 上各个景点都有电瓶车代步 司机都好牛 那个山路绕来绕去的 感觉像过山车哈哈 看见了绣面老人 深切感受到了那个年代的文化 我没有请导游到各个景点都有导游在那讲 感觉这边导游说话都特别幽默 里边还有米酒免费品尝 喝了一小杯 特别好喝 我是个女孩子都特别喜欢 拿了一瓶哈哈价格也不贵 去的朋友可以去尝尝 里边有卖银饰的 我在里边买了一个银镯子 有点小贵 但是有意义的都值了 还有好多话 打字太累了就不说了 去三亚一定要去槟榔谷感受一下 ', '2016-10-01 18:52:24', '-1916168271:http://p0.meituan.net/shaitu/d3d6c471ef53c7bf25253d826104f055449345.jpg;\n-1915904360:http://p0.meituan.net/shaitu/84ff8975cd9eda16ce2c1ebfdf77f90b534973.jpg;\n-1916567858:http://p1.meituan.net/shaitu/ac7e577e8e15ad62a3fa93d89b81bdd5352977.jpg;\n-1916140811:http://p1.meituan.net/shaitu/9bf8b50437bc52750311514c0a65ac55594596.jpg;\n-1916205989:http://p0.meituan.net/shaitu/632c1481c6a831619dce6a57367ef855658339.jpg;\n-1916789054:http://p0.meituan.net/shaitu/6051064a7b54b5555a3fffc966879801518611.jpg;\n-1915904344:http://p1.meituan.net/shaitu/152fd47b5aa074928a2eee5ebe215c16479064.jpg;\n-1916131889:http://p1.meituan.net/shaitu/f1d7e62dce2c1a2e90e61dc20438ef35579619.jpg;\n', 'None');
