INSERT INTO `meituan_binglanggu` VALUES ('3656', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '美丽的大山深处有一个美丽的槟榔谷，迎着椰风海韵来到这个少数民族型5a级景区真的是获益匪浅，这里的讲解员各有风格和特色，一路上带给我们不少欢乐，神秘的黎家文化和香甜的山栏酒让我们沉醉其中，一声热情的波隆~体会到了黎族人的热情好客。', '2016-09-03 22:41:41', '-1922385178:http://p0.meituan.net/shaitu/07eec0a46fe03651ef8c1909620b298732047.jpg;\n-1921901297:http://p1.meituan.net/shaitu/10f576569ca1acabff3373c2a796418238346.jpg;\n', 'None');
