INSERT INTO `meituan_binglanggu` VALUES ('3673', 'Ouh676717113', '400209549', '', '黎族还挺独特的，不管是他们的长寿文化还是他们独有的女权文化都很迷人，这里的阿妹都很好看，高高瘦瘦的跟我们汉族人没什么区别，她们的笑容特别甜，这里的人文景观比自然景观好看多了，听说长寿的老人家有八九十岁了，很难得。', '2016-08-27 19:11:37', '-1923736383:http://p1.meituan.net/shaitu/ef1ae5ed4c863227f01eaab2d333300284888.jpg;\n-1924323014:http://p1.meituan.net/shaitu/cfc90d2c5841174e93232843ab7240a8107307.jpg;\n-1923765741:http://p1.meituan.net/shaitu/964d46c945818971da0c20afd2da30d7137956.jpg;\n-1923776661:http://p1.meituan.net/shaitu/bb7d9b23747a40e2f247725519cf90cb102629.jpg;\n-1923792199:http://p1.meituan.net/shaitu/7858e96b5ce6cb152c7ee4cce0ea1e81112191.jpg;\n', 'None');
