INSERT INTO `meituan_binglanggu` VALUES ('3687', '猎狗吻香', '168430885', 'https://img.meituan.net/avatar/d44187f4351e382ec37838da02e33304268977.jpg', '来三亚的第二天旅游，直接从网上预定的槟榔谷门票，既方便有实惠，槟榔谷很美很好玩，来第一次还想再来第二次，比我想象中的还美，很有文化气息，跟着爸妈一次三亚之旅，爸妈刚回到酒店，说玩了一整天很累，但值得啦，出来旅游都是这样，只有玩的开心就好，给店家点个赞！', '2016-08-23 20:24:11', '-1924911237:http://p1.meituan.net/shaitu/caa7c096cb581880052aa4fc6137ed2c265113.jpg;\n-1924839307:http://p1.meituan.net/shaitu/f3b89f0a70580c7517ddcaaf81f948b9414995.jpg;\n-1924769776:http://p0.meituan.net/shaitu/cf7a8cf227a8fcda331afb7ee430f6ef573029.jpg;\n', 'None');
