INSERT INTO `meituan_binglanggu` VALUES ('3719', 'lgl994249229', '155303168', '', '第一次来海南，和爸妈一起！槟榔谷还是很不错的。黎族是海南的土著民族，这里当然保存着最原始、最淳朴的黎族风情。拉龟、射箭、荡秋千、攀藤摘花、挑山栏过河、过独木桥等体育项目，鼻萧、椰乌、吹树叶等乐器表演，唱歌、跳竹竿舞等娱乐项目，射鱼、贵屋等生活方式，织锦等手工技能无不体现了浓郁的少数民族风情。值得一去！', '2016-08-10 16:00:25', '-2013861184:http://p0.meituan.net/shaitu/3d5df2d4b1c2894dae1117fa44ebd96f295799.jpg;\n-2013860613:http://p1.meituan.net/shaitu/5ec9b0299cef3b5f23e0322af6fe5aab440031.jpg;\n-2013810281:http://p1.meituan.net/shaitu/4f8d42dc5bc46c152901bb032b406716298610.jpg;\n', 'None');
