INSERT INTO `meituan_binglanggu` VALUES ('4077', 'CDd855255909', '298042417', 'https://img.meituan.net/avatar/3fab3aa7e5e3ada62b2a4aafb62e7d59229743.jpg', '风景很美呀，热带雨林噢，跟家人出来游玩特别好，原本自己去的，结果海南的出租车太不靠谱了，不打表乱喊价，提前一天订好。第二天早晨七点左右开始今天的行程，我们主外亚龙湾距离景区比较远，还好有大巴车一路带领特别棒！', '2016-03-05 18:29:15', '-1929183038:http://p0.meituan.net/shaitu/30496017e295e4537bcf7186f2074acc1151395.jpg;\n-1929136518:http://p1.meituan.net/shaitu/b9e9be01f9e27b81563e47933cf5c8751168473.jpg;\n-1928594557:http://p0.meituan.net/shaitu/28c9ee9b1ec01ffc01df14d0ff1ee34c949794.jpg;\n', 'None');
