INSERT INTO `meituan_binglanggu` VALUES ('4079', 'FcC31865058', '82362264', '', '离市区坐车不会很久，在三亚市区坐班车，12元一位，里面不大一个两个小时基本就能逛完，有一条小吃街，有挺多吃的，但就是不明白这里跟台湾文化有什么关系，非要套个台湾文化，美食，下午出去市区有司机拉散客，20一位。', '2016-03-05 10:43:37', '-1935253546:http://p1.meituan.net/shaitu/d8dc816d793f291d160c8652e30b6d9a316469.jpg;\n-1934842541:http://p1.meituan.net/shaitu/996db80600093dcac7e48a47ca9be625263043.jpg;\n-1934841489:http://p1.meituan.net/shaitu/000a9fb43d541c421e929df260994d40424607.jpg;\n-1934834405:http://p1.meituan.net/shaitu/22b2f4d129c45153b282a1cef5a95ca5441858.jpg;\n', 'None');
