INSERT INTO `meituan_binglanggu` VALUES ('4081', 'LD18697108976', '11593052', '', '换票很方便，帮家人订的，希望他们玩得开心，很早就去过，记得还不错的，保留原有的原生态，是现在社会最需要的，挺喜欢这种旅游开发项目，挺好，美美的每天去全国各地转转，世界辣么大，值得走一走，美团上一些还是很划算的，越来越方便也是不错的', '2016-03-05 10:23:28', '-1935352310:http://p1.meituan.net/shaitu/c3a461b709f28bdb2f43fb6f081c3a2c69866.jpg;\n-1934748603:http://p0.meituan.net/shaitu/dc02939986a68a1ae0555a6c56d60f4964779.jpg;\n-1934763673:http://p0.meituan.net/shaitu/99de5321044c8a93ab354a2a3d3add3447334.jpg;\n', 'None');
