INSERT INTO `meituan_binglanggu` VALUES ('4338', '_weixin368992744', '209978521', 'https://img.meituan.net/avatar/90baed43843bbc3e9a36e2de3dc52fe9218782.jpg', '景色特别美！那个小导游特别好！玩的很开心，值得一去', '2016-01-25 09:12:00', '-1931381285:http://p0.meituan.net/shaitu/f9206f5d8a655a0efede993f0e015fdb986261.jpg;\n', 'None');
INSERT INTO `meituan_binglanggu` VALUES ('4339', 'full1125', '41959276', '', '出票很快，是我到海南第一个游玩的地方，算是黎族文化的聚集地，我们做的观光车，基本每个景点都去过了，每个景点都有下一站和到出口的车，非常方便，值得推荐', '2016-01-24 22:03:51', '', 'None');
INSERT INTO `meituan_binglanggu` VALUES ('4340', 'ZWc876941702', '69729790', '', '4年前来过。里面没有原来好，而且商业化比较严重，很多东西都在建。演出要单独花钱。', '2016-01-24 16:20:41', '', 'None');
