INSERT INTO `meituan_binglanggu` VALUES ('4453', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '这可以说是来三亚必须到的地方吧，一个地方的历史、风俗、文化、民族是了解这个地方的前提，里面的表演非常有特色，就是物价有点高，最好是自己带上吃的喝的，还有就是所有景区的通病吧，太有点铜臭气了，这让景区减分不少吧!', '2015-12-16 11:45:30', '-1935358433:http://p1.meituan.net/shaitu/e745f62acadc93dbb70c1b2cb553bf2097342.jpg;\n-1935342515:http://p1.meituan.net/shaitu/cec4d73aa9d9c43268c8dc9a87cbad4865801.jpg;\n-1935156280:http://p0.meituan.net/shaitu/689e4f534aba3c8e6260ea66447d728e62815.jpg;\n', 'None');
