INSERT INTO `meituan_binglanggu` VALUES ('4478', 'KoF883311884', '76045939', '', '团购价格要比自由行便宜很多，导游也是很热情很认真负责。古韵表演挺好看的，只是导游说，要在表演中才能看到满身纹身的阿婆其实在景区中，也是可以看到跟他们合影的，来到这里可以了解，浓浓的民族风情和他们的一些生活习惯，感觉增长了不少知识，带着孩子来看一看还是很不错的。', '2015-12-05 18:06:27', '-1927170698:http://p1.meituan.net/shaitu/46f38324794d06ee00e8a3236c23ecc4329863.jpg;\n', 'None');
