INSERT INTO `meituan_binglanggu` VALUES ('4492', 'vpf580524732', '57700786', '', '这是我团最划算的一次，导游叫我们买看表演的门票，我们没有买，也有别的表演看，所以亲们注意不要被坑了，吃饭呢菜还行，这个价也是值得的了，导游也挺幽默的，只是出门在外导游叫买什么，都不要忙，一般都是不值这个价钱，其他都还行', '2015-11-28 00:36:40', '-1932238381:http://p0.meituan.net/shaitu/b1bf05c80e408273bd77128b2d182584308183.jpg;\n-1932068308:http://p0.meituan.net/shaitu/5a970d79ad0b3173f07341c55fd83cc0244229.jpg;\n-1932014808:http://p0.meituan.net/shaitu/4472495c0a4fabb5ca72ccfde42c80ee413114.jpg;\n', 'None');
