INSERT INTO `meituan_binglanggu` VALUES ('4673', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '美团券是VIP，请导游还便宜30，导游就是黎家阿哥，特别可爱，带我们试了很多东西，自制酒啊，槟榔烟啊，里面的游览车凭票可以随便坐，滑索很赞啊，就是等的时间长，玩只有一会。最要注意的就是一定要提前预定，要等一个小时才能进园区的', '2015-07-31 16:31:25', '-1933148459:http://p0.meituan.net/shaitu/58acf8b48a51bc961a3ceffc7d69567a357260.jpg;\n-1933087104:http://p0.meituan.net/shaitu/8beea22df1d7a2c00b1dfba082b7a2a2528828.jpg;\n-1933560674:http://p1.meituan.net/shaitu/eb62f236adeccc6a2b05a02afd40df93344706.jpg;\n', 'None');
